  data: () => ({
    tips: [],
    dialogInnerHeight: 0,
    dialogInnerWidth: 0,
    selectIndex: null
  }),
  methods: {
    onResize: function (event) {
      this.dialogInnerHeight = event.cardHeight;
      this.dialogInnerWidth = event.cardwidth;
    },
    show: async function (tips) {
      this.tips = tips;
      await this.$refs.Dialog.show();
    },
    hasLinkInfo: function (linkInfo) {
      let hasLinkInfo = false;
      if (linkInfo.length >= 1) hasLinkInfo = true;
      return hasLinkInfo;
    },
    onClick: function (linkInfo) {
      if (linkInfo["link"]) window.open(linkInfo["link"]);
    }
  }
};
